import Foundation
import KeychainAccess
import ObjectMapper
import RxSwift
import RxCocoa
import AppAuth

let loggedIn = BehaviorRelay<Bool>(value: false)

class AuthManager {
    
    static let shared = AuthManager()

    // MARK: - Properties
    fileprivate let tokenKey = "TokenKey"
    fileprivate let keychain = Keychain(service: Configs.App.bundleIdentifier)
    
    let tokenChanged = PublishSubject<Token?>()

    init() {
        loggedIn.accept(hasValidToken)
        user.accept(User.currentUser())
        searchHistory.accept(SearchHistoryItem.currentAllHistory())
    }

    var token: Token? {
        get {
            #if DEVELOP
            return Token(basicToken: " eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICJ0SHpZOWZCWElOQ1d2R2xwMnp6ZkphcU5WNHhYbDc0MU9ranZURUNjb1hJIn0..P4o4Sx7dnh2GEmxANHjuIFwAU6-ABD-JC8rEz844O45CvLyVYcv-lMjsvMFpd8SSP0e4e9r-udNAc3FAAn4Xw7RSJAsT_BsOHZdP9in01MQeSESepZC-axeaJrXlST30A9ryVOZhEicL6LbgBPxvcc0eQ7k5S_B9Ryxk2UH1WGeKSZ9zj6ffHB-EO6fS1sSAdMG6x54aGNltfGGfDbiuRgJ_mv4awnTGsZJtE6iPYTR0exD5qnPKcbC6Q-UveNuWy9DE3OkCp4gI_6uxqmCuYcDN-aHRrCI25Rng5IgJ4m7f2MCYKzEtSQw26FXnUuq381QYi_Q37BXc1B40eq0oPw")
            #else
            guard let jsonString = keychain[tokenKey] else { return nil }
            return Token(JSONString: jsonString)
            #endif
        }
        set {
            if let token = newValue, let jsonString = token.toJSONString() {
                keychain[tokenKey] = jsonString
            } else {
                keychain[tokenKey] = nil
            }
            tokenChanged.onNext(newValue)
            loggedIn.accept(hasValidToken)
        }
    }

    var hasValidToken: Bool {
        return token?.isValid == true
    }

    class func setToken(token: Token) {
        AuthManager.shared.token = token
    }

    class func removeToken() {
        AuthManager.shared.token = nil
    }

    class func tokenValidated() {
        AuthManager.shared.token?.isValid = true
    }
}
